mapper.insert:
insert into mainDb.tb_menu(menu_nm, menu_pc, menu_desc, menu_stock, menu_disp_yn, menu_img_nm)
                          values(
                              #{menuNm}
                              ,#{menuPc}
                              ,#{menuDesc}
                              ,#{menuStock}
                              ,#{menuDispYn}
                              ,#{menuImgNm}
                          )

mapper.select:
select *
  from mainDb.tb_menu
 where 1=1
   
       and menu_no = #{menuNo}
   
     
       and menu_nm like CONCAT('%',#{menuNm},'%')
   
   
       and menu_disp_yn = #{menuDispYn}

mapper.update:
 update mainDb.tb_menu
    set menu_nm = #{menuNm}
       ,menu_desc = #{menuDesc}
       ,menu_pc = #{menuPc}
       ,menu_stock = #{menuStock}
       ,menu_disp_yn = #{menuDispYn}
where menu_no = #{menuNo}
