mapper.insert:
insert into MAINDB.TB_ORD(ord_dt, menu_no, menu_nm, ord_qty, ord_amt, ord_stat)
            values(
                NOW()
                ,#{menuNo}
                ,#{menuNm}
                ,#{ordQty}
                ,#{ordAmt}
                ,#{ordStat}
            )

mapper.update:
update MAINDB.TB_MENU
   set menu_stock = menu_stock - #{ordQty}
 where menu_no = #{menuNo}
